mapper.select:

select i_id, i_title, i_time, i_content, eid, i_infotype
from tbl_information
where i_id = #{iId,jdbcType=CHAR}

SELECT i.i_id, i.i_title, i.i_time, i.i_content, e.e_user_name eid, d.value i_infotype FROM tbl_information i JOIN tbl_employee e ON e.eid=i.eid JOIN tbl_dic_value d ON d.id=i.i_infotype
i.i_title like '%' #{iTitle} '%'
AND i.i_infotype=#{iInfotype}
AND i.i_time>=#{startDate}
AND #{endDate}>=i.i_time

SELECT i_id, i_title, i_time, i_content, eid, i_infotype
FROM tbl_information
WHERE i_id=#{id}

mapper.delete:
