mapper.insert:

insert into tbl_registered (rid, r_date, r_department, 
  r_price, pid, p_phone, p_name, 
  p_type, p_breed, eid, doctor
  )
values (#{rid,jdbcType=CHAR}, #{rDate,jdbcType=CHAR}, #{rDepartment,jdbcType=CHAR}, 
  #{rPrice,jdbcType=REAL}, #{pid,jdbcType=CHAR}, #{pPhone,jdbcType=CHAR}, #{pName,jdbcType=VARCHAR}, 
  #{pType,jdbcType=VARCHAR}, #{pBreed,jdbcType=VARCHAR}, #{eid,jdbcType=CHAR}, #{doctor,jdbcType=CHAR}
  )

mapper.select:
SELECT r.rid, r.r_date, d1.value r_department, r.r_price, po.p_user_name pid, r.p_phone, r.p_name, d2.value p_type, r.p_breed,
e.e_name eid, r.doctor
FROM tbl_registered r
JOIN tbl_pet_owner po ON po.pid=r.pid
JOIN tbl_dic_value d1 ON d1.id=r.r_department
LEFT JOIN tbl_dic_value d2 ON d2.id=r.p_type
JOIN tbl_employee e ON e.eid=r.eid


    AND r.r_department=#{department}


    AND r.p_phone=#{phone}


    AND r.r_date=#{date}

mapper.update:
UPDATE tbl_registered
SET r_department=#{rDepartment}, r_price=#{rPrice}, pid=#{pid}, p_phone=#{pPhone}, p_name=#{pName},
p_type=#{pType}, p_breed=#{pBreed}, eid=#{eid}, doctor=#{doctor}
WHERE rid=#{rid}

mapper.delete:
DELETE FROM tbl_registered
